package Interview;

public class EvenNumbers {
    public static void main(String[] args) {
        // TODO Auto-generated method stub


        //Task-40
        //Write a program that displays all even numbers between 1-100 in same line

        for(int z=1;z<=100;z++) {

            if(z%2==0) {
                System.out.println(z);

            }
        }


    }
}
